import React, {useState} from 'react';
import {Lista} from './components/Lista'

export function App()
{
	const[listaItems, setLista] = useState([
		{id: 1, tarea: "Tarea 1", completado:false}
	]);
	return (
	<div>
	<Lista listado={listaItems}/>
	<input type="text" placeholder="Nueva tarea"/>
	<button type="Agregar"/>
	</div>
	);
}
