import React  from 'react';

 export default function Campos({inputText, setInputText, items, setItems})
{
	const inputTextHandler = (e) =>
	{
		console.log(e.target.value);
		setInputText(e.target.value);
	} 
	const submitHandler = (e) =>
	{
		e.preventDefault();
		setItems([
			...items, {text: inputText, completed: false, id: Math.random()}
		]);
		setInputText("");

	}
	return (
		 <form>
      <input value={inputText} type="text" className="form-input" onChange={inputTextHandler}/>
      <button className="form-button" type="submit" onClick={submitHandler}>
        <i className="boton"></i>
      </button>
      <div className="select">
        <select name="lista2" className="filter">
          <option value="todas">Todas</option>
          <option value="completadas">Completadas</option>
          <option value="incompletas">Sin completar</option>
        </select>
      </div>
    </form>

	)
}
