import React  from 'react';

export default function Item({text, item, items, setItems})
{
	const deleteHandler = () =>
	{
		setItems=(items.filter((el) => el.id !== item.id));
	}
 return(
     <div className="items">
		 <li className="item">{text} </li>
			 <button className="botonEliminar" onClick={deleteHandler}>
				 <i className="boton"></i>
			 </button>
		
	 </div>
 )
}
